mapper.select:
SELECT ar.id, ar.asset_name_id, ar.asset_type_id, ar.amount, ar.unit_id, ar.asset_location_id, ar.acquire_time, ar.remark, ar.create_time, ar.update_time, ar.create_user, ar.update_user, ar.version, ar.deleted, COALESCE(an.name, '') as asset_name, COALESCE(type_meta.type_name, '') as asset_type_name, COALESCE(type_meta.value1, '') as asset_type_value, COALESCE(unit_meta.type_name, '') as unit_name, COALESCE(unit_meta.value1, '') as unit_value, COALESCE(location_meta.type_name, '') as location_name, COALESCE(location_meta.value1, '') as location_value FROM asset_record ar LEFT JOIN asset_name an ON ar.asset_name_id = an.id LEFT JOIN common_meta type_meta ON ar.asset_type_id = type_meta.id AND type_meta.type_code = 'ASSET_TYPE' LEFT JOIN common_meta unit_meta ON ar.unit_id = unit_meta.id AND unit_meta.type_code = 'CURRENCY' LEFT JOIN common_meta location_meta ON ar.asset_location_id = location_meta.id AND location_meta.type_code = 'ASSET_LOCATION'
AND ar.asset_name_id = #{assetNameId}
AND ar.asset_location_id = #{locationId}
AND ar.asset_type_id = #{typeId}
AND ar.remark LIKE CONCAT('%', #{remark}, '%')
AND ar.acquire_time >= #{startDate}
AND ar.acquire_time <= #{endDate}
AND ar.create_user = #{createUser}
AND ar.deleted = 0
ORDER BY ar.acquire_time DESC
